// Creating arrays
const numbers = [1, 2, 3, 4, 5];

// Accessing elements - O(1)
console.log(numbers[0]); // 1

// Common operations
numbers.push(6);           // Add to end - O(1)
numbers.pop();             // Remove from end - O(1)
numbers.unshift(0);        // Add to start - O(n)
numbers.shift();           // Remove from start - O(n)

// Array methods
const doubled = numbers.map(n => n * 2);
const filtered = numbers.filter(n => n > 2);
const sum = numbers.reduce((acc, n) => acc + n, 0);

// Find two numbers that add up to target
function twoSum(arr, target) {
  let left = 0;
  let right = arr.length - 1;

  while (left < right) {
    const sum = arr[left] + arr[right];

    if (sum === target) {
      return [left, right];
    } else if (sum < target) {
      left++;
    } else {
      right--;
    }
  }

  return [-1, -1];
}

// Example
const arr = [1, 2, 3, 4, 6];
console.log(twoSum(arr, 6)); // [1, 3]

class Node {
  constructor(data) {
    this.data = data;
    this.next = null;
  }
}

class LinkedList {
  constructor() {
    this.head = null;
  }

  // Add node at beginning - O(1)
  insertAtHead(data) {
    const newNode = new Node(data);
    newNode.next = this.head;
    this.head = newNode;
  }

  // Search - O(n)
  search(data) {
    let current = this.head;
    while (current) {
      if (current.data === data) return true;
      current = current.next;
    }
    return false;
  }
}

class TreeNode {
  constructor(val) {
    this.val = val;
    this.left = null;
    this.right = null;
  }
}

// Inorder Traversal (Left, Root, Right)
function inorderTraversal(root) {
  const result = [];

  function traverse(node) {
    if (!node) return;
    traverse(node.left);
    result.push(node.val);
    traverse(node.right);
  }

  traverse(root);
  return result;
}

// Example
const root = new TreeNode(1);
root.left = new TreeNode(2);
root.right = new TreeNode(3);

function bubbleSort(arr) {
  const n = arr.length;

  for (let i = 0; i < n - 1; i++) {
    let swapped = false;

    for (let j = 0; j < n - i - 1; j++) {
      if (arr[j] > arr[j + 1]) {
        // Swap
        [arr[j], arr[j + 1]] = [arr[j + 1], arr[j]];
        swapped = true;
      }
    }

    // If no swaps, array is sorted
    if (!swapped) break;
  }

  return arr;
}

// Example
const arr = [64, 34, 25, 12, 22];
console.log(bubbleSort(arr));
